-- ----------------------------
--  Table structure for `hslib_book_class`
-- ----------------------------
DROP TABLE IF EXISTS `hslib_book_class`;
CREATE TABLE `hslib_book_class` (
  `book_id` int(11) NOT NULL,
  `class_id` varchar(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='书本所属类别表';

-- ----------------------------
--  Table structure for `hslib_book_collect`
-- ----------------------------
DROP TABLE IF EXISTS `hslib_book_collect`;
CREATE TABLE `hslib_book_collect` (
  `c_list_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '收藏清单id',
  `book_id` int(255) DEFAULT NULL COMMENT '书id',
  `person_id` int(11) DEFAULT NULL COMMENT '人id',
  `collect_status` varchar(255) DEFAULT NULL COMMENT '收藏状态：',
  `collect_desc` varchar(255) DEFAULT NULL COMMENT '收藏描述',
  PRIMARY KEY (`c_list_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='图书收藏表';

-- ----------------------------
--  Table structure for `hslib_book_comment`
-- ----------------------------
DROP TABLE IF EXISTS `hslib_book_comment`;
CREATE TABLE `hslib_book_comment` (
  `comment_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '反馈表id',
  `person_id` int(11) DEFAULT NULL COMMENT '人员id',
  `book_id` int(11) DEFAULT NULL COMMENT '书本id',
  `comment_status` varchar(255) DEFAULT NULL COMMENT '反馈状态',
  `marks` varchar(5) DEFAULT ' ' COMMENT '分数',
  `comments` varchar(255) DEFAULT NULL COMMENT '评论',
  `if_best` varchar(255) DEFAULT NULL COMMENT '是否最佳评论',
  PRIMARY KEY (`comment_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='反馈信息表';

