-- ----------------------------
--  Table structure for `hslib_book_liked`
-- ----------------------------
DROP TABLE IF EXISTS `hslib_book_liked`;
CREATE TABLE `hslib_book_liked` (
  `a_list_id` int(11) NOT NULL COMMENT '点赞推荐主id',
  `person_id` int(11) DEFAULT NULL COMMENT '人员id',
  `book_id` int(11) DEFAULT NULL COMMENT '书本ID',
  `approved` varchar(255) DEFAULT NULL COMMENT '是否喜爱，推荐',
  `recommend` varchar(255) DEFAULT NULL COMMENT '推荐',
  `recommend_desc` varchar(255) DEFAULT NULL COMMENT '推荐描述',
  PRIMARY KEY (`a_list_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='喜爱推荐表';

-- ----------------------------
--  Table structure for `hslib_book_tree`
-- ----------------------------
DROP TABLE IF EXISTS `hslib_book_tree`;
CREATE TABLE `hslib_book_tree` (
  `class_id` varchar(11) NOT NULL,
  `sub_classid` varchar(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='分类层级表。';

-- ----------------------------
--  Table structure for `hslib_book_wanted`
-- ----------------------------
DROP TABLE IF EXISTS `hslib_book_wanted`;
CREATE TABLE `hslib_book_wanted` (
  `w_list_id` int(11) NOT NULL COMMENT '清单id',
  `person_id` int(11) DEFAULT NULL,
  `status` varchar(255) DEFAULT NULL COMMENT '0-建立想看；1-正在处理；2-完成处理',
  `book_name` varchar(255) DEFAULT NULL COMMENT '书名',
  `description` varchar(255) DEFAULT NULL COMMENT '描述',
  PRIMARY KEY (`w_list_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='想看清单表';

-- ----------------------------
--  Table structure for `hslib_books`
-- ----------------------------
DROP TABLE IF EXISTS `hslib_books`;
CREATE TABLE `hslib_books` (
  `book_id` int(255) NOT NULL AUTO_INCREMENT COMMENT '书id',
  `booksets_id` int(255) DEFAULT NULL COMMENT '书集id',
  `book_no` varchar(255) DEFAULT NULL COMMENT '图书编码',
  `on_off_shelf` varchar(255) DEFAULT NULL COMMENT '上下架',
  `updatetime` datetime DEFAULT NULL COMMENT '时间',
  `operator` int(10) DEFAULT NULL COMMENT '最后操作员id',
  `order_status` varchar(2) DEFAULT NULL COMMENT '每一个明细的书都有一个状态',
  PRIMARY KEY (`book_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='图书表，主体表。';

