-- ----------------------------
--  Table structure for `hslib_booksets`
-- ----------------------------
DROP TABLE IF EXISTS `hslib_booksets`;
CREATE TABLE `hslib_booksets` (
  `booksets_id` int(11) NOT NULL AUTO_INCREMENT,
  `book_name` varchar(255) DEFAULT NULL COMMENT '书名',
  `author` varchar(255) DEFAULT NULL COMMENT '作者',
  `class_id` int(255) DEFAULT NULL COMMENT '分类ID',
  `picture` varchar(255) DEFAULT NULL COMMENT '图片路径',
  `description` varchar(255) DEFAULT NULL COMMENT '书本描述',
  `is_recommend` varchar(255) DEFAULT NULL COMMENT '首页1，图书借阅2',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `total_amount` int(4) DEFAULT NULL COMMENT '书本总数量',
  `left_amount` int(4) DEFAULT NULL COMMENT '剩余可借出数量',
  `on_off_shelf` varchar(2) DEFAULT ' ' COMMENT '书籍 是否上架 0 上架  1 不上架',
  PRIMARY KEY (`booksets_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=256 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

