DROP TABLE IF EXISTS `hslib_person`;
CREATE TABLE `hslib_person` (
  `person_id` int(255) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `person_name` varchar(255) DEFAULT NULL,
  `sex` varchar(255) DEFAULT NULL,
  `mail` varchar(255) DEFAULT NULL,
  `use_status` varchar(255) DEFAULT NULL COMMENT '是否有效，可以被注销掉',
  `use_role` varchar(255) DEFAULT NULL COMMENT '角色，是否是管理员',
  `account_balance` int(255) DEFAULT NULL COMMENT '账户余额',
  `balance_freezed` int(255) DEFAULT NULL COMMENT '冻结金额',
  `experience` int(255) DEFAULT NULL COMMENT '经验值',
  `fines_total` int(255) DEFAULT NULL COMMENT '累计罚款',
  PRIMARY KEY (`person_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='人物表';

