-- ----------------------------
--  Procedure structure for `p_AutoCheck`
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_AutoCheck`;
delimiter ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `p_AutoCheck`(OUT `p_error_type` int)
BEGIN

	/*
	自动扫描，检查订单状态包括：
	          1 customer预定书籍时，没有及时取书，导致自动取消
						2 过期未还书的数据，更新订单头状态
	*/

declare v_order_id ,v_line_id ,v_booksets_id,v_person_id ,v_book_id int DEFAULT 0;
declare done int;	
	# 创建游标，并存储数据
declare cur_id CURSOR for    
   select m.order_id,m.person_id,s.booksets_id,m.book_id
     from hslib.hslib_order_main m ,hslib.hslib_books s
    where m.order_status = '1' #预约
     and s.book_id = m.book_id 
	   and DATEDIFF(now(),m.order_datetime) > 7;
	
# 游标中的内容执行完后将done设置为1
 DECLARE CONTINUE HANDLER FOR NOT FOUND SET done=1; 
# 打开游标
open cur_id;
# 执行循环
  posLoop:LOOP
# 判断是否结束循环
		IF done=1 THEN  
      LEAVE posLoop;
    END IF; 
# 取游标中的值
    FETCH  cur_id into v_order_id,v_person_id,v_booksets_id,v_book_id;
# 执行操作
     update hslib.hslib_order_line set is_last_line = 'N'
      where order_id = v_order_id and is_last_line = 'Y'; 

     select max(line_id) into v_line_id from hslib.hslib_order_line where order_id = v_order_id;
#插入一条最新的数据，状态为取消订单
     insert into hslib.hslib_order_line(order_id,line_id,order_status,is_last_line,order_date,operatorpersion_id)
		   values (v_order_id,v_line_id+1,'-1','Y',now(),-1);#使用程序自动运行，更改人设置成-1
			 
			  #需要更新数据，将图书馆中的该书总数量增加一
			update hslib_booksets set left_amount = left_amount+1 
			where booksets_id = v_booksets_id;
			
			#人员的冻结金额数据减少 20 
			update hslib.hslib_person set balance_freezed = balance_freezed - 20 
			where person_id = v_person_id;
			
			#更改书本的状态
			update hslibs.hslib_books set order_status = '-1' where book_id = v_book_id;
    
  END LOOP posLoop;
# 释放游标
set p_error_type = 1 ;#默认为1，表示错误的。
	
#更改订单状态
	update hslib.hslib_order_main m 
	set m.order_status = '0'	#取消预约
	where m.order_status = '1' #预约
	and DATEDIFF(now(),m.order_datetime) > 7;
#第一部分数据处理逻辑处理完毕

#第二部分数据处理，到期的数据，更改主订单状态 行数据状态，天数
update hslib.hslib_order_main m 
	set m.is_delay = 'Y'	#延期
	where m.order_id in 
	(select l.order_id from hslib.hslib_order_line l
	  where l.is_last_line = 'Y'
		and l.order_status in ('2','3')
		and l.return_date < now())
	and m.order_status in ('2','3');#借出或者续借的书
	
update 	hslib.hslib_order_line l
set l.delay_days = datediff(now(),l.return_date),l.delay_status = 'Y'
where l.is_last_line = 'Y'
 and l.order_status in ('2','3')
 and l.return_date < now();
set p_error_type = 0 ;#设置为成功状态
	commit;


END
 ;;
delimiter ;

-- ----------------------------
--  Procedure structure for `p_ConsoleOperate`
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_ConsoleOperate`;
delimiter ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `p_ConsoleOperate`(IN `p_order_id` int,IN `p_operate_type` int,IN `p_operatorpersion_id` int,IN `p_message` VARCHAR(255),OUT `p_error_type` int)
BEGIN
/*
借阅管理，后台操作员使用界面，主要功能点3个：取消预定-1；确定借书出库2；还书入库-2。
返回消息状态 默认为1（失败）成功则为0

*/


DECLARE ord_num ,v_order_id ,v_line_id ,v_booksets_id,v_book_id ,v_person_id ,v_fined_fee_id INT DEFAULT 0;

declare fine_no int default  0; #单独设置一变量表示是否存在罚款的数量
declare fine_amount int default 0;#罚款金额，默认为0 
set p_error_type = 1 ;#默认为1，表示错误的。
#获取书籍id
select b.booksets_id ,m.person_id,m.book_id into v_booksets_id ,v_person_id,v_book_id
from hslib_books b,hslib_order_main m
where b.book_id = m.book_id
and m.order_id = p_order_id
;

	#控制台操作将图书出库 2，确定还书 -2，取消订单 -1 
	if p_operate_type = 2 then #借出图书,图书出库
	   select count(1) into ord_num 
		 from hslib_order_main 
		 where order_id = p_order_id and order_status = '1';#已经预约状态
	   if ord_num > 0 then
	     update hslib.hslib_order_main 
		   set order_status = '2',operatorpersion_id = p_operatorpersion_id		 
		   where order_id = p_order_id
		   and order_status = '1';
			 
			 select max(line_id) into v_line_id 
			 from hslib.hslib_order_line where  order_id = p_order_id;
			 
			 update hslib.hslib_order_line set is_last_line = 'N' 
			 where order_id = p_order_id and is_last_line = 'Y';
			 
			 #图书借出，增加一条记录
			 insert into hslib.hslib_order_line(order_id,line_id,order_status,is_last_line,order_date,return_date,operatorpersion_id)
			 values (p_order_id,v_line_id+1,'2','Y',now(),DATE_ADD(now(),INTERVAL 1 MONTH),p_operatorpersion_id);
			 
			 update hslib.hslib_books set order_status = '2' where book_id = v_book_id;
		 end if;
		 set p_error_type = 0 ;#设置为成功状态
	ELSEIF p_operate_type = -1 then #操作员取消预约
	   select count(1) into ord_num 
		 from hslib_order_main 
		 where order_id = p_order_id and order_status = '1';#已经预约状态
	   if ord_num > 0 then
	     update hslib.hslib_order_main 
		   set order_status = '-1',operatorpersion_id = p_operatorpersion_id		 
		   where order_id = p_order_id
		   and order_status = '1';
			 
			 select max(line_id) into v_line_id 
			 from hslib.hslib_order_line where  order_id = p_order_id;
			 
			 update hslib.hslib_order_line set is_last_line = 'N' 
			 where order_id = p_order_id and is_last_line = 'Y';
			 
			 #取消图书预定，增加一条记录
			 insert into hslib.hslib_order_line(order_id,line_id,order_status,is_last_line,order_date,operatorpersion_id,description )
			 values (p_order_id,v_line_id+1,'-1','Y',now(),p_operatorpersion_id, p_message);#p_message
			 #增加图书数量，人员的经验取消冻结金额20
			 update hslib_booksets set left_amount = left_amount+1 
			 where booksets_id = v_booksets_id;	
			 
			 update hslib.hslib_person set balance_freezed = balance_freezed - 20 
			 where person_id = v_person_id;
			
			 update hslib.hslib_books set order_status = '-1' where book_id = v_book_id;
		 end if;
	  set p_error_type = 0 ;#设置为成功状态
	ELSEIF p_operate_type = -2 then #还书入库
/*还书，先计算是否有罚款金额，1 有罚款金额；2 无。
1 先计算出，罚款金额，写入罚款行表，设置变量罚款ID v_fined_fee_id 
2不增加记录到行表。

不论是否有罚款数据，均将 v_fined_fee_id 值写到行表 0表示没有关联罚款表

   */
		 #过期的书籍
		 select count(1) into ord_num 
		 from hslib_order_main 
		 where order_id = p_order_id 
		 and is_delay = 'Y'#延期了
		 and (order_status = '3' or order_status = '2') ;#
		 
		 #延期的书籍需要罚款
		 if ord_num > 0 then
			  select count(1) into fine_no 
				from hslib.hslib_order_fined f,hslib.hslib_order_main m
				where f.person_id = m.person_id and f.book_id = m.book_id
				and m.order_id = p_order_id;
				if fine_no > 0 then
				#获取最新的罚款ID，在后面还需要使用默认为0表示没有罚款的id
				   select max(f.fined_id)  into v_fined_fee_id
					 from hslib.hslib_order_fined f,hslib.hslib_order_main m
				   where f.person_id = m.person_id and f.book_id = m.book_id
				   and m.order_id = p_order_id;
				   end if;				 
					 
					 set v_fined_fee_id = v_fined_fee_id + 1;
					 
					 select datediff(now(),l.return_date) into  fine_amount
					 from hslib.hslib_order_line l 
					 where l.order_id = p_order_id
					 and l.is_last_line = 'Y';
					 
		    insert hslib.hslib_order_fined(fined_id,person_id,book_id,fined_amount,fined_date,operator_id)
				select  v_fined_fee_id,m.person_id,m.book_id,fine_amount,now(),p_operatorpersion_id
				from hslib.hslib_order_main m
				where m.order_id = p_order_id				;
				
				#增加借书人员的罚款金额
				update hslib.hslib_person p set p.fines_total = fines_total + fine_amount
				where p.person_id = v_person_id;
		 end if;
	
	   
	   #正常还书
	   select count(1) into ord_num 
		 from hslib_order_main 
		 where order_id = p_order_id and order_status = '2' ;#已经借出状态
	   if ord_num > 0 then
	     update hslib.hslib_order_main 
		   set order_status = '-2',operatorpersion_id = p_operatorpersion_id		 
		   where order_id = p_order_id
		   and order_status = '2';
			 
			 select max(line_id) into v_line_id 
			 from hslib.hslib_order_line where  order_id = p_order_id;
			 
			 update hslib.hslib_order_line 
			 set is_last_line = 'N' , real_return_date = now(),
			     delay_status = case when datediff(now(),return_date)>0 then 'Y' else 'N' end,
					 delay_days = case when datediff(now(),return_date)>0 then datediff(now(),return_date) else 0 end
			 where order_id = p_order_id and is_last_line = 'Y' and order_status = '2';
			 
			 #正常还书，增加一条记录   罚款ID使用的上面获取的，如果上面没有产生罚款，则为0 
			 insert into hslib.hslib_order_line(order_id,line_id,order_status,is_last_line,order_date,operatorpersion_id,fined_fee_id)
			 values (p_order_id,v_line_id+1,'-2','Y',now(),p_operatorpersion_id,v_fined_fee_id);
			 #增加图书数量，人员的经验取消冻结金额20
			 update hslib_booksets set left_amount = left_amount+1 
			 where booksets_id = v_booksets_id;	
			 
			 update hslib.hslib_person set balance_freezed = balance_freezed - 20 
			where person_id = v_person_id;
			
			update hslib.hslib_books set order_status = '-2' where book_id = v_book_id;
		 end if;
		 
		 #续借后还书
	   select count(1) into ord_num 
		 from hslib_order_main 
		 where order_id = p_order_id and order_status = '3' ;#已经续借状态
	   if ord_num > 0 then
	     update hslib.hslib_order_main 
		   set order_status = '-3',operatorpersion_id = p_operatorpersion_id		 
		   where order_id = p_order_id
		   and order_status = '3';
			 
			 select max(line_id) into v_line_id 
			 from hslib.hslib_order_line where  order_id = p_order_id;
			 
			 update hslib.hslib_order_line 
			 set is_last_line = 'N' , real_return_date = now(),
			     delay_status = case when datediff(now(),return_date)>0 then 'Y' else 'N' end,
					 delay_days = case when datediff(now(),return_date)>0 then datediff(now(),return_date) else 0 end
			 where order_id = p_order_id and is_last_line = 'Y' and order_status = '3';
			 
			 #续借图书还书，增加一条记录
			 insert into hslib.hslib_order_line(order_id,line_id,order_status,is_last_line,order_date,operatorpersion_id,fined_fee_id)
			 values (p_order_id,v_line_id+1,'-3','Y',now(),p_operatorpersion_id,v_fined_fee_id);
			 #增加图书数量，人员的经验取消冻结金额20
			 update hslib_booksets set left_amount = left_amount+1 
			 where booksets_id = v_booksets_id;	
			 
			 update hslib.hslib_person set balance_freezed = balance_freezed - 20 
			where person_id = v_person_id;
			
			update hslib.hslib_books set order_status = '-3' where book_id = v_book_id;
		 end if;
		 set p_error_type = 0 ;#设置为成功状态
	END IF;
	commit;

END
 ;;
delimiter ;

-- ----------------------------
--  Procedure structure for `p_CustomOrdered`
-- ----------------------------
DROP PROCEDURE IF EXISTS `p_CustomOrdered`;
delimiter ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `p_CustomOrdered`(IN `p_book_id` int,IN `p_person_id` int,IN `p_order_type` int,OUT `p_error_type` int)
BEGIN
/*
传入参数中的p_order_type数据使用 1（预定） 2（借书出库） 3（续借） -1（取消预定） -2（还书入库） -3（续借的书还书入库） 
本过程使用到的参数主要有 1  3  -1三个值。
*/
DECLARE ord_num ,v_order_id ,v_line_id ,v_booksets_id ,v_book_id INT DEFAULT 0;
declare v_person_id int ;
set v_person_id = p_person_id;
set v_book_id = p_book_id;
select booksets_id into v_booksets_id from hslib_books where book_id = p_book_id;
#确认借书，客户借书，传进来的参数是1 

set p_error_type = 1 ;#默认为1，表示错误的。
if p_order_type = 1 then  
#客户端下订单，预约书籍。
#先判断是否存在借书以及订单的情况
 		select count(1) into ord_num
 		from hslib.hslib_order_main where person_id = p_person_id and book_id = p_book_id;
 		if ord_num > 0 then
 		update hslib.hslib_order_main set order_status = '1' , order_datetime = now(),operatorpersion_id = null
		where person_id = p_person_id and book_id = p_book_id and order_status <>'1';
 		else
 		#增加一条预定的记录
			insert into hslib.hslib_order_main(book_id,person_id,order_status,order_datetime)
			values (p_book_id,p_person_id,'1',NOW());
			end if;
			select order_id into v_order_id
			from hslib.hslib_order_main where person_id = p_person_id and book_id = p_book_id;
	
	#判断是否含有数据
			select count(1) into ord_num 
			from hslib.hslib_order_line where order_id = v_order_id ;
			if ord_num > 0 then
			#原订单数据存在
			select max(line_id) into v_line_id from hslib.hslib_order_line where  order_id = v_order_id ;
			else
			set v_line_id = 0;
			end if ;
			#避免数据重复使用控制,重复数据保护
 		 select count(1) into ord_num 
			from hslib.hslib_order_line 
			where order_id = v_order_id 
			and line_id = v_line_id and is_last_line = 'Y' and order_status = '1'; 
			#已经有数据了，不需要重复下单
	
			if ord_num = 0 then
	
			#将之前订单行数据全部更新为非最新
			update hslib.hslib_order_line set is_last_line = 'N' where order_id = v_order_id and is_last_line = 'Y';
	
			#做一个的动作就往行表里面写一条记录，时间记录为预约时点
			insert into hslib.hslib_order_line(order_id,line_id,order_status,is_last_line,order_date)
			values (v_order_id,v_line_id+1,'1','Y',now());
			#需要更新数据，将图书馆中的该书总数量减少一
			update hslib_booksets set left_amount = left_amount-1 
			where booksets_id = v_booksets_id;
			
			#人员的冻结金额数据增加 20 
			update hslib.hslib_person set balance_freezed = balance_freezed +20 
			where person_id = v_person_id;
			
			#更改书本状态
			update hslib.hslib_books set order_status = '1' where book_id = v_book_id;
			end if;
	set p_error_type = 0 ;#运行结束数据表示正确		
	ELSEIF p_order_type = -1 then  #客户取消订单,取消借阅
			  #先处理订单
		select count(1) into ord_num  
		from hslib.hslib_order_main where person_id = p_person_id and book_id = p_book_id
		and order_status = '1';
		if ord_num > 0 then 
		   select order_id into v_order_id 
		     from hslib.hslib_order_main where person_id = p_person_id and book_id = p_book_id
		     and order_status = '1';
				 
		   update hslib.hslib_order_main set order_status = '-1' ,order_datetime = now(),operatorpersion_id = null
			 where person_id = p_person_id and book_id = p_book_id and order_status = '1';
			 
				 #将该订单下面的订单行数据全部更新为历史
				 update hslib.hslib_order_line set is_last_line = 'N' 
				 where order_id = v_order_id and is_last_line = 'Y'; 
				 #获取最新的一条数据
				 select max(line_id) into v_line_id from hslib.hslib_order_line where  order_id = v_order_id ;
				 #插入一条最新的数据，状态为取消订单
				 insert into hslib.hslib_order_line(order_id,line_id,order_status,is_last_line,order_date)
				 values (v_order_id,v_line_id+1,'-1','Y',now());
				 
				 #需要更新数据，将图书馆中的该书总数量增加一
			update hslib_booksets set left_amount = left_amount+1 
			where booksets_id = v_booksets_id;
			
			#人员的冻结金额数据减少 20 
			update hslib.hslib_person set balance_freezed = balance_freezed - 20 
			where person_id = v_person_id;
			
			#更改书本状态
			update hslib.hslib_books set order_status = '-1' where book_id = v_book_id;
		end if;
	set p_error_type = 0 ;#运行结束数据表示正确	
	ELSEIF 	p_order_type = 3 then  #客户续借图书
	   #判断目标数据是否合理
		/* select count(1) into ord_num  
		 from hslib.hslib_order_main where person_id = p_person_id and book_id = p_book_id
		 and order_status = '3';
		 if ord_num > 0 then
		   leave lable_end ;
		 #系统仅仅允许客户续借一次图书
		 end if ;
	   #null;
		 */
		 #判断目标数据是否存在，
		 select count(1) into ord_num  
		 from hslib.hslib_order_main where person_id = p_person_id and book_id = p_book_id
		 and order_status = '2';
		 if ord_num > 0 then
		 #存在，修改订单main信息，
		 select order_id into v_order_id 
		     from hslib.hslib_order_main where person_id = p_person_id and book_id = p_book_id
		     and order_status = '2';
				 
		 update hslib.hslib_order_main set order_status = '3' ,order_datetime = now(),operatorpersion_id = null
		 where person_id = p_person_id and book_id = p_book_id and order_status = '2';
		 
		 #存在，修改行信息，新增一条记录到行表		 
		 update hslib.hslib_order_line set is_last_line = 'N' 
				 where order_id = v_order_id and is_last_line = 'Y'; 
				 
		select max(line_id) into v_line_id from hslib.hslib_order_line where  order_id = v_order_id ;
		#插入一条最新的数据，状态为续借图书
		insert into hslib.hslib_order_line(order_id,line_id,order_status,is_last_line,order_date,return_date)
				 values (v_order_id,v_line_id+1,'3','Y',now(),DATE_ADD(now(),INTERVAL 1 MONTH));	 
		
   #更改书本状态
			update hslib.hslib_books set order_status = '3' where book_id = v_book_id;
		
		 end if;
		 
set p_error_type = 0 ;#运行结束数据表示正确	
	end if;
		
	
	#存储过程最后，提交事务
	
	commit;
	

END
 ;;
